import React from 'react'
import { SliderData } from './SliderData'
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import  './Slider.css'
import { Divider, Paper,Button, Typography,makeStyles } from '@material-ui/core';


const useStyles=makeStyles(theme=>({
    paper:{
        height:'auto',
        padding:'10px 20px ',
         position:'absolute',
         background:'rgba(0,0,0,0.5)',
         textAlign:"center",
         transform:'translate(-50%,40%)',
         top:'10%',
         left:'50%',
         width:'40%',
         [theme.breakpoints.down('sm')]:{
                width:'65%',
                
         }
    
    }
       

}))

 
       




const Slider = ({slides}) => {
    const classes=useStyles()
    const [current,setcurrent]=React.useState(0);
    const length=slides.length

    const prevSlide=()=>{
      setcurrent(current===0 ? length-1 : current-1 );
    }
    const nextSlide=()=>{
        setcurrent(current===length-1 ? 0 : current+1);
    }
    if (!Array.isArray(slides) || slides.length<=0){
        return null;
    }




    return (
     

        <section className="slider" >
            <ArrowBackIosIcon  className="left" onClick={prevSlide} />
           <ArrowForwardIosIcon className="right" onClick={nextSlide}  />
          
       {
         
           SliderData.map((img,index)=>{
              
            return(
              <div className={index===current? 'slide active' :'slide'}  key={index} >
              {index===current && (
                   <img className="image" src={img.image} alt="img" />
             
                   
                   
                   ) }
                  
                  <Paper  className={classes.paper} >
                    <Typography variant="h1" style={{fontSize:'2rem',lineHeight:'2.6rem',color:'#fff'}} color="initial"> Check Our Available Rooms</Typography>
                    <div style={{width:'15%',margin:' .5rem auto'}} >
                    <Divider style={{height:'.3rem',background:"red"}} varient="middle" />
                    </div>
                    <Typography variant="body2" style={{color:"#fff"}} >Luxurious Room Starting Price at Rs.500</Typography>

                    <div style={{margin:' 1rem 0 ' , display:'flex' , justifyContent:'space-evenly'  }} >
                    <Button variant="contained" color="secondary">
                         View Rooms
                              </Button>
                              <Button variant="contained" color="secondary">
                         Book Now
                              </Button>
                        
                        
                        </div>

                   </Paper> 
                  
                  </div>
 
           )})

       }

        </section>
 
    )
}

export default Slider
