import React from 'react'
import { SliderData } from './SliderData'
import  './Slider.css'
import { Divider, Paper,Button,Box ,Grid,Typography,makeStyles } from '@material-ui/core';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';


const useStyles=makeStyles(theme=>({
    slider:{
        position: 'relative',
        height: '88vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        width: '100%',
    },
  
    slide:{
        position: 'absolute',
        width: '100%',
        top: '0',
         height: 'auto',
     opacity: '0',
     transitionDuration: '0.5s ease',
     
    },

    image:{
        width: '100%',
        height:'88vh',
        objectFit: 'cover',
    },

    active:{
        opacity: 1,
      transitionDuration:'0.5s',
    transform: 'scale(1)',
    },

    paper:{
  
        height:'auto',
        zIndex:15,
        background:'rgba(0,0,0,0.5)',
       lineHeight:'2rem',
         padding:'10px 20px',
         [theme.breakpoints.down('sm')]:{
                width:'70%',
                fontSize:'50px'
            
                
        },

       
         body:{
             color:"#fff"
         }

      
    },


       

}))

 
       




const Slider = ({slides}) => {
    const classes=useStyles()
    const [current,setcurrent]=React.useState(0);
    const length=slides.length

    const prevSlide=()=>{
      setcurrent(current===0 ? length-1 : current-1 );
    }
    const nextSlide=()=>{
        setcurrent(current===length-1 ? 0 : current+1);
    }
    if (!Array.isArray(slides) || slides.length<=0){
        return null;
    }




    return (
     
        
        <section className={classes.slider} >
                 <ArrowBackIosIcon  className="left" onClick={prevSlide} />
                   <ArrowForwardIosIcon className="right" onClick={nextSlide}  />
       {
          

         
           SliderData.map((img,index)=>{
              
            return(
              <div className={index===current? 'slide active' : classes.slide}  key={index} >
              {index===current && (
                   <img className={classes.image} src={img.image} alt="img" />
             
                   
                   
                   ) }
                  
        


                  
                  </div>

           )})


       }

                                          
                        <Paper className={classes.paper} >
                 <Typography style={{fontSize:"4rem",fontWeight:600,color:'red'}} >
                   Luxurious Rooms
                    </Typography>
                           
                      <Typography  style={{color:"#fff",textAlign:'center',marginBottom:"1rem"}} variant="body1"  >
                   Best Place to Stay and have fun 
                    </Typography>
                      <div style={{display:'flex',justifyContent:"center",padding:"0 1rem "}} >
                     <Button style={{margin:"0 1rem "}} variant="contained" color="secondary">
                                    Rooms
                          </Button>
                            <Button style={{margin:"0 1rem "}} variant="contained" color="secondary">
                                  Book Now
                                </Button>
                                  </div>
                             </Paper>
                                                   
                       
 

 

        </section>
      
        
 
    )
}

export default Slider
