import React,{useState} from 'react'
import {MenuItems} from './MenuItems'
import  './Navbar.css'


const Navbar = () => {
   const [value,changevalue] = useState(false)
   const clicked=()=>{
     changevalue(!value)
   }
    return (
        <>
          <nav className="NavClass">

              <h1 className="Logo" >  Sunset Lodge </h1>
              <div className="Menu-Icon" onClick={clicked} >
                  <i className={value? 'fas fa-times':'fas fa-bars'} ></i>
               </div>


              <ul className={value ? 'nav-menu active' : 'nav-menu' } >
                  {
                      MenuItems.map(
                          (item,index)=>{
                              return(
                                 <li key={index}  ><a className={item.cName} href={item.url} >{item.title}</a></li>
                              )
                          }
                      
                      )
                    }
                   
                 
              </ul>
              <a href="#" className="button" >Book Now</a>
              

          </nav>

        </>
    )
}

export default Navbar
