import React from 'react'
import {Typography,Grid,makeStyles} from '@material-ui/core'
import FacebookIcon from '@material-ui/icons/Facebook';
import TwitterIcon from '@material-ui/icons/Twitter';
import InstagramIcon from '@material-ui/icons/Instagram';
import YouTubeIcon from '@material-ui/icons/YouTube';
import RoomIcon from '@material-ui/icons/Room';
import PhoneIcon from '@material-ui/icons/Phone';
import EmailIcon from '@material-ui/icons/Email';

const useStyles=makeStyles(theme=>({
root:{
    flexGrow:1,
 width:"100%",
 height:"auto",
 backgroundColor:"#181b52",
 marginTop:"3rem",
 paddingTop:"3rem"

 



},
heading:{
    color:"#fff",
    textAlign:"left",
    fontSize:"2rem"

},
sci:{
    display:"flex",
    justifyContent:"start",
    padding:'1rem 1rem '
},
micon:{
    display:"inline-block",
    backgroundColor:"#222",
    display:"flex",
    justifyContent:"center",
    alignItems:"center",
    width:"40px",
    height:"40px",
    marginRight:"10px",
    textDecoration:"none",
    borderRadius:"4px",

    '&:hover': 
    { 
        background: "#f00", 
    }

  
},
i:{
    fontSize:"1.5rem",
    textAlign:"left",
    color:"#fff"

   
},
li:{
    listStyleType:"none"
},

qlink:{



},
quick:{
    listStyleType:"none",
    textAlign:'left',
    color:"#fff"
},
l:{
    color:"#999",
    textDecoration:"none",
    display:"block",
    '&:hover': 
    { 
        color: "#fff", 
    }
},
location:{
    listStyleType:"none",

},
llocation:{
    listStyleType:"none",
    marginBottom:"1rem",
    display:"flex",
    justifyContent:"start"
    
    
},
licon:{
    color:"#fff",
    marginRight:".7rem"
},
name:{
    color:"#999",
    lineHeight:"1.7rem",
    '&:hover': 
    { 
        color: "#fff", 
    }
},

grid:{
    marginBottom:"1rem"
}






}))

const Footer = () => {
    const classes=useStyles()
    return (
        <div>
            <Grid container className={classes.root} 
                    direction="row"
                    justifyContent="space-around"
                   
                       
                       >
                <Grid  items xs={12} sm={12} md={3}>
                    <div className={classes.grid} >
                 <Typography className={classes.heading} >About Us</Typography>

                 <Typography style={{color:"#999",textAlign:"left"}} variant="body1" color="inherit">
                 Lorem ipsum dolor sit amet consectetur adipisicing elit. 
                     Aspernatur fuga recusandae cumque aperiam voluptatibus placeat itaque,
                    deleniti fugit sunt repellat autem praesentium perferendis quae tempore, error facilis aliquid qui delectus.
                 </Typography>
                 
                 <ul className={classes.sci}>
                  <li className={classes.li} > <a href="#" className={classes.micon} > <FacebookIcon className={classes.i} /> </a> </li>
                  <li className={classes.li}  > <a href="#" className={classes.micon} > <TwitterIcon className={classes.i}  /> </a> </li>
                  <li className={classes.li}  > <a href="#" className={classes.micon}  > <InstagramIcon className={classes.i}  /> </a> </li>
                  <li className={classes.li}  >   <a href="#" className={classes.micon}  > <YouTubeIcon className={classes.i}  /> </a> </li>
                 
                 </ul>
                 
                 </div>

                </Grid>

                <Grid  items xs={12} sm={12} md={3}  >
                    <div className={classes.grid} >
                        <Typography className={classes.heading} style={{textAlign:"left"}}  >Quick Links</Typography>

                        <ul className={classes.quick} >
                            <li style={{marginBottom:'1rem'}} ><a href="#" className={classes.l} >About</a></li>
                            <li style={{marginBottom:'1rem'}} ><a href="#" className={classes.l} >Offer</a></li>
                            <li style={{marginBottom:'1rem'}} ><a href="#" className={classes.l} >Contact</a></li>
                            <li style={{marginBottom:'1rem'}} ><a href="#" className={classes.l} >Book Now</a></li>



                        </ul>
                       </div>

                    </Grid>

                    <Grid items xs={12} sm={12} md={3}  >
                        <div  className={classes.grid} >
                     <Typography className={classes.heading} style={{textAlign:"left"}} >Contact Info</Typography>
                     <ul className={classes.location} >
                         <li className={classes.llocation} >
                              <span><RoomIcon className={classes.licon} /></span>
                              <span className={classes.name} > Bhaktapur Street No.247 <br/> <span>Kathmandu , Nepal</span>  </span>
                             </li>
                         

                         <li className={classes.llocation}  >
                              <span><PhoneIcon  className={classes.licon} /></span>
                              <span className={classes.name}  > 9865434342 <br/> 023 540765 </span>

                             </li>
                             <li className={classes.llocation}  >
                              <span><EmailIcon className={classes.licon} /></span>
                              <span className={classes.name} > ramkumar@gmail.com <br/> harigupta@gmail.com </span>

                             </li>
                         </ul>
                          </div>
                        </Grid>

                </Grid>



        </div>
    )
}

export default Footer
