import React, {useEffect} from 'react'
import { useParams } from 'react-router-dom';
import { useSelector,useDispatch } from 'react-redux';
import { Grid } from '@material-ui/core';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
    root: {
      maxWidth: 460,
      
    },
    media: {
      minHeight: 340,
    },
    grid:{
        padding:"1rem"
    }
  });

const FilterFeature = () => {
    const classes = useStyles();
    const {type} =useParams();
     const dispatch = useDispatch();
     useEffect(() => {
       
dispatch({type:"FEATURED",tp:type})

     }, [type])

    const {room}=useSelector(state=>state.roomsReducer)

    return (
        <>
                     < Grid container  >


               
                     
{
    room.map(r =>(

   
<Grid className={classes.grid} item md={6} sm={6} xs={12}>

<div style={{display:"flex",justifyContent:"center"}}  >

<Card className={classes.root}>
<CardMedia
component="img"
alt="Contemplative Reptile"
height="200"
image={ process.env.PUBLIC_URL + r.image }
title={r.type}
/>
<CardContent>
<Typography gutterBottom style={{fontSize:"1rem",textTransform:"capitalize"}} >
{r.type} Room
</Typography>
<div  style={{display:"flex",justifyContent:"start"}} >
<Typography variant="body2" color="textSecondary" gutterBottom component="p">
Facility : 
</Typography>
<Typography variant="body2" color="textSecondary" component="p">
&nbsp; {r.Facility}
</Typography>
</div>
<div style={{display:"flex",justifyContent:"start"}} >
<Typography variant="body2" gutterBottom color="textSecondary" component="p">
Capacity : 
</Typography>
<Typography variant="body2" color="textSecondary" component="p">
&nbsp; {r.capacity}
</Typography>
</div>
<div style={{display:"flex",justifyContent:"start"}} >
<Typography variant="body2" color="textSecondary" gutterBottom component="p">
Price : 
</Typography>
<Typography variant="body2" color="textSecondary" component="p">
&nbsp; {r.Price} per-night
</Typography>
</div>
</CardContent>
<CardActions>
<Button size="small" color="primary">
Book Now
</Button>

</CardActions>
</Card>



</div>

 </Grid>

))}








</Grid>

      
       
        </>
    )
}

export default FilterFeature
