import { Grid } from '@material-ui/core'
import React from 'react'
import Navbar from '../../components/navbar/Navbar'
import { useSelector } from 'react-redux'
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Filter from './Filter';


const useStyles = makeStyles({
    root: {
      maxWidth: 460,
      
    },
    media: {
      minHeight: 340,
    },
    grid:{
        padding:"1rem"
    }
  });

const Rooms = () => {
    const classes = useStyles();

const {rooms} =useSelector(state=>state.roomsReducer)

    return (
        <>
        <Navbar/>

        <Filter/>
           < Grid container style={{width:"90%",margin:"0 auto"}} >


               
                     
                     {
                         rooms.map(room =>(

                        
               <Grid className={classes.grid} item md={4} sm={6} xs={12}>

                  <div style={{display:"flex",justifyContent:"center"}}  >

                  <Card className={classes.root}>
        <CardMedia
          component="img"
          alt="Contemplative Reptile"
          height="200"
          image={ process.env.PUBLIC_URL + room.image }
          title={room.type}
        />
        <CardContent>
        <Typography gutterBottom style={{fontSize:"1rem",textTransform:"capitalize"}} >
            {room.type} Room
          </Typography>
           <div  style={{display:"flex",justifyContent:"center"}} >
          <Typography variant="body2" color="textSecondary" gutterBottom component="p">
           Facility : 
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
          &nbsp; {room.Facility}
          </Typography>
          </div>
          <div style={{display:"flex",justifyContent:"start"}} >
          <Typography variant="body2" color="textSecondary" component="p">
           Capacity : 
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
          &nbsp; {room.capacity}
          </Typography>
          </div>
          <div style={{display:"flex",justifyContent:"start"}} >
          <Typography variant="body2" color="textSecondary" component="p">
           Price : 
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
          &nbsp; {room.Price}
          </Typography>
          </div>
        </CardContent>
      <CardActions>
        <Button size="small" color="primary">
          Book Now
        </Button>
 
      </CardActions>
    </Card>


               
                </div>

                      </Grid>

                     ))}


                  


        


               </Grid>



        </>
    )
}

export default Rooms
