import React from 'react'
import { SliderData } from './SliderData'
import  './Slider.css'
import { Divider, Paper,Button,Box ,Grid,Typography,makeStyles } from '@material-ui/core';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';


const useStyles=makeStyles(theme=>({
    slider:{
        position: 'relative',
        height: '88vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        width: '100%',
    },
  
    slide:{
        position: 'absolute',
        width: '100%',
        top: '0',
         height: 'auto',
     opacity: '0',
     transitionDuration: '0.5s ease',
     
    },

    image:{
        width: '100%',
        height:'88vh',
        objectFit: 'cover',
    },

    active:{
        opacity: 1,
      transitionDuration:'0.5s',
    transform: 'scale(1)',
    },

    paper:{
        display:'flex',
        flexDirection:'column',
        alignItems:'center',
        justifyContent:'center',
        height:'auto',
        zIndex:15,
         textAlign:"center",
         color:"red",
       
        padding:'10px 20px',
         [theme.breakpoints.down('sm')]:{
                
            
                
         },

         heading:{
               
         }

      
    },


       

}))

 
       




const Slider = ({slides}) => {
    const classes=useStyles()
    const [current,setcurrent]=React.useState(0);
    const length=slides.length

    const prevSlide=()=>{
      setcurrent(current===0 ? length-1 : current-1 );
    }
    const nextSlide=()=>{
        setcurrent(current===length-1 ? 0 : current+1);
    }
    if (!Array.isArray(slides) || slides.length<=0){
        return null;
    }




    return (
     
        
        <section className={classes.slider} >
                 <ArrowBackIosIcon  className="left" onClick={prevSlide} />
                   <ArrowForwardIosIcon className="right" onClick={nextSlide}  />
       {
          

         
           SliderData.map((img,index)=>{
              
            return(
              <div className={index===current? 'slide active' : classes.slide}  key={index} >
              {index===current && (
                   <img className={classes.image} src={img.image} alt="img" />
             
                   
                   
                   ) }
                  
        


                  
                  </div>

           )})


       }

                                          
                                             <Paper className={classes.paper} >
                                                    <Typography className={classes.heading} >
                                                        Luxurious Rooms
                                                     </Typography>
                           
                                                     <Typography variant="body1"  color="initial">
                                                         Best Place to Stay and have fun 
                                                     </Typography>
                                                     <div>
                                                     <Button variant="contained" color="secondary">
                                                        Rooms
                                                       </Button>
                                                       <Button variant="contained" color="secondary">
                                                              Book Now
                                                               </Button>
                                                         </div>
                                                </Paper>
                                                   
                       
 

 

        </section>
      
        
 
    )
}

export default Slider
